<script setup>
import { ref, onMounted } from 'vue'
import gsap from 'gsap'

const textPath = ref(null)
const isBig = ref(false)

function animatePath() {
  const length = textPath.value.getTotalLength()

  gsap.set(textPath.value, {
    strokeDasharray: length,
    strokeDashoffset: length,
  })

  gsap.to(textPath.value, {
    strokeDashoffset: 0,
    duration: 2.5,
    ease: 'power2',
  })
}

setInterval(() => {
  isBig.value = true
}, 300)

onMounted(() => {
  animatePath()
})
</script>

